import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background: #121212;
  color: #FFFFFF;
  font-family: 'Inter', sans-serif;
`;

const Header = styled.div`
  padding: 20px;
  text-align: center;
  background: linear-gradient(180deg, rgba(255,255,255,0.05) 0%, rgba(255,255,255,0) 100%);
`;

const Balance = styled.div`
  font-size: 28px;
  font-weight: 600;
  margin-bottom: 4px;
`;

const Disclaimer = styled.div`
  font-size: 12px;
  color: #B3B3B3;
`;

const AssetSelector = styled.div`
  display: flex;
  overflow-x: auto;
  padding: 15px;
  background: rgba(255,255,255,0.02);
  gap: 15px;
`;

const Asset = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  opacity: ${props => props.active ? 1 : 0.7};
`;

const AssetIcon = styled.div`
  width: 48px;
  height: 48px;
  border-radius: 50%;
  background: ${props => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 8px;
  color: white;
  font-weight: 600;
`;

const AssetPrice = styled.div`
  font-size: 14px;
  color: #FFFFFF;
`;

const AssetChange = styled.div`
  font-size: 12px;
  color: ${props => props.positive ? '#00C853' : '#FF3D57'};
`;

const ChartContainer = styled.div`
  flex: 1;
  background: #121212;
  padding: 20px;
  position: relative;
`;

const TimeIntervals = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 8px;
  padding: 15px;
`;

const IntervalButton = styled.button`
  background: ${props => props.active ? 'rgba(255,255,255,0.1)' : 'transparent'};
  border: 1px solid rgba(255,255,255,0.1);
  border-radius: 20px;
  color: #FFFFFF;
  padding: 8px;
  cursor: pointer;
  font-size: 14px;

  &:hover {
    background: rgba(255,255,255,0.05);
  }
`;

const TradingControls = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 15px;
  padding: 15px;
`;

const TradeButton = styled.button`
  padding: 15px;
  border-radius: 8px;
  border: none;
  color: #FFFFFF;
  font-size: 16px;
  font-weight: 600;
  cursor: pointer;
  display: flex;
  flex-direction: column;
  align-items: center;
  background: ${props => props.type === 'buy' 
    ? 'linear-gradient(135deg, #00C853 0%, #009624 100%)'
    : 'linear-gradient(135deg, #FF3D57 0%, #D50000 100%)'};

  &:hover {
    opacity: 0.9;
  }
`;

const ButtonPrice = styled.div`
  font-size: 12px;
  opacity: 0.8;
  margin-top: 4px;
`;

const Navigation = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  padding: 15px;
  background: rgba(255,255,255,0.02);
  border-top: 1px solid rgba(255,255,255,0.05);
`;

const NavButton = styled.button`
  background: transparent;
  border: none;
  color: ${props => props.active ? '#FFFFFF' : '#B3B3B3'};
  font-size: 12px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
  cursor: pointer;
`;

function TradingSimulator() {
  const [activeAsset, setActiveAsset] = useState('BTC');
  const [activeInterval, setActiveInterval] = useState('1MIN');
  const [activeTab, setActiveTab] = useState('chart');

  const assets = [
    { id: 'GOLD', name: 'Gold', price: '1824', change: '+1.2', color: '#FFD700' },
    { id: 'TSLA', name: 'TSLA', price: '190', change: '-0.5', color: '#E31937' },
    { id: 'AAPL', name: 'AAPL', price: '180', change: '+2.1', color: '#666666' },
    { id: 'BTC', name: 'BTC', price: '52k', change: '+0.8', color: '#F7931A' },
  ];

  const intervals = ['1MIN', '5MIN', '10MIN', '30MIN', '1H', '1D', '1W', '1M'];

  return (
    <Container>
      <Header>
        <Balance>$ -9,236.34</Balance>
        <Disclaimer>This is not real money</Disclaimer>
      </Header>

      <AssetSelector>
        {assets.map(asset => (
          <Asset 
            key={asset.id}
            active={activeAsset === asset.id}
            onClick={() => setActiveAsset(asset.id)}
          >
            <AssetIcon color={asset.color}>{asset.name[0]}</AssetIcon>
            <AssetPrice>${asset.price}</AssetPrice>
            <AssetChange positive={asset.change.startsWith('+')}>
              {asset.change}%
            </AssetChange>
          </Asset>
        ))}
      </AssetSelector>

      <ChartContainer>
        {/* TradingView chart will be integrated here */}
        <div style={{ color: '#666', textAlign: 'center', paddingTop: '40%' }}>
          Chart coming soon...
        </div>
      </ChartContainer>

      <TimeIntervals>
        {intervals.map(interval => (
          <IntervalButton
            key={interval}
            active={activeInterval === interval}
            onClick={() => setActiveInterval(interval)}
          >
            {interval}
          </IntervalButton>
        ))}
      </TimeIntervals>

      <TradingControls>
        <TradeButton type="sell">
          SELL
          <ButtonPrice>$9,566</ButtonPrice>
        </TradeButton>
        <TradeButton type="buy">
          BUY
          <ButtonPrice>$9,568</ButtonPrice>
        </TradeButton>
      </TradingControls>

      <Navigation>
        <NavButton 
          active={activeTab === 'home'}
          onClick={() => setActiveTab('home')}
        >
          ‚åÇ
          <span>Home</span>
        </NavButton>
        <NavButton 
          active={activeTab === 'chart'}
          onClick={() => setActiveTab('chart')}
        >
          üìà
          <span>Chart</span>
        </NavButton>
        <NavButton 
          active={activeTab === 'settings'}
          onClick={() => setActiveTab('settings')}
        >
          ‚öôÔ∏è
          <span>Settings</span>
        </NavButton>
      </Navigation>
    </Container>
  );
}

export default TradingSimulator;
