import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import TradingViewChart from './components/TradingViewChart';
import ChallengeSelector from './components/ChallengeSelector';
import LearningPath from './components/LearningPath';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
`;

const Title = styled.h1`
  color: #333;
  margin-bottom: 20px;
  text-align: center;
`;

const Controls = styled.div`
  margin: 20px 0;
  display: flex;
  gap: 10px;
  justify-content: center;
`;

const Input = styled.input`
  padding: 8px;
  border: 1px solid #ddd;
  border-radius: 4px;
  width: 150px;
  font-size: 16px;
`;

const Button = styled.button`
  padding: 8px 16px;
  background-color: #2196F3;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.2s;
  font-size: 16px;

  &:hover {
    background-color: #1976D2;
  }
`;

const Result = styled.div`
  margin: 20px auto;
  padding: 15px;
  border-radius: 4px;
  background-color: ${props => props.success ? '#e8f5e9' : '#ffebee'};
  max-width: 600px;
  text-align: center;
  line-height: 1.5;
`;

const generateCandlestickData = (n_days = 50, startPrice = 100) => {
  const data = [];
  let currentPrice = startPrice;

  for (let i = 0; i < n_days; i++) {
    const date = new Date();
    date.setDate(date.getDate() - (n_days - i));

    const change = (Math.random() - 0.5) * 4;
    currentPrice += change;

    const dailyVolatility = Math.abs(Math.random() - 0.5) * 2;
    const openPrice = currentPrice + (Math.random() - 0.5) * dailyVolatility;
    const highPrice = Math.max(openPrice, currentPrice) + Math.abs(Math.random()) * dailyVolatility;
    const lowPrice = Math.min(openPrice, currentPrice) - Math.abs(Math.random()) * dailyVolatility;

    data.push({
      time: date.toISOString().split('T')[0],
      open: Number(openPrice.toFixed(2)),
      high: Number(highPrice.toFixed(2)),
      low: Number(lowPrice.toFixed(2)),
      close: Number(currentPrice.toFixed(2))
    });
  }

  return data;
};

function App() {
  const [pastData, setPastData] = useState([]);
  const [futureData, setFutureData] = useState([]);
  const [tpValue, setTpValue] = useState(100);
  const [result, setResult] = useState(null);
  const [currentChallenge, setCurrentChallenge] = useState(1);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Generate initial data
    const data = generateCandlestickData();
    const midPoint = Math.floor(data.length / 2);

    setPastData(data.slice(0, midPoint));
    setFutureData(data.slice(midPoint));
    setTpValue(data[midPoint].close);
    setIsLoading(false);
  }, []);

  const handleChallengeChange = (challenge) => {
    setCurrentChallenge(challenge);
    setResult(null);
    if (pastData.length > 0) {
      setTpValue(pastData[pastData.length - 1].close);
    }
  };

  const handleTPSubmit = () => {
    if (!futureData.length) return;

    const maxPrice = Math.max(...futureData.map(d => d.high));
    const entryPrice = futureData[0].open;

    let hitDate = null;
    let profit = 0;

    for (const candle of futureData) {
      if (candle.high >= tpValue) {
        hitDate = candle.time;
        profit = ((tpValue - entryPrice) / entryPrice) * 100;
        break;
      }
    }

    if (currentChallenge === 1) {
      // Challenge 1: Check if TP is within 2 units of highest point
      if (maxPrice - 2 <= tpValue && tpValue <= maxPrice) {
        setResult({
          success: true,
          message: `Excellent! Your TP of $${tpValue.toFixed(2)} is within 2 units of the highest point ($${maxPrice.toFixed(2)})`
        });
      } else {
        setResult({
          success: false,
          message: `Try again! Your TP should be between $${(maxPrice - 2).toFixed(2)} and $${maxPrice.toFixed(2)}`
        });
      }
    } else {
      // Challenge 2: Check if TP was hit
      if (hitDate) {
        const accuracy = (1 - Math.abs(maxPrice - tpValue) / maxPrice) * 100;
        setResult({
          success: true,
          message: `Success! Your TP of $${tpValue.toFixed(2)} was reached on ${hitDate}\n` +
                  `Profit: ${profit.toFixed(1)}%\n` +
                  `Accuracy: ${accuracy.toFixed(1)}%`
        });
      } else {
        setResult({
          success: false,
          message: `Your TP of $${tpValue.toFixed(2)} was not reached in the future period.\n` +
                  `The highest price was $${maxPrice.toFixed(2)}`
        });
      }
    }
  };

  const handleTPLineMove = (price) => {
    setTpValue(Number(price.toFixed(2)));
  };

  if (isLoading) {
    return (
      <Container>
        <Title>Loading...</Title>
      </Container>
    );
  }

  return (
    <Container>
      <Title>Trading Simulator</Title>

      {/* Add Learning Path section */}
      <LearningPath />

      <ChallengeSelector 
        currentChallenge={currentChallenge}
        onChallengeChange={handleChallengeChange}
      />

      {pastData.length > 0 && futureData.length > 0 && (
        <TradingViewChart 
          pastData={pastData}
          futureData={futureData}
          showFuture={currentChallenge === 1}
          onTPLineMove={handleTPLineMove}
        />
      )}

      <Controls>
        <Input
          type="number"
          value={tpValue}
          onChange={(e) => setTpValue(Number(e.target.value))}
          step="0.01"
          placeholder="Enter TP value"
        />
        <Button onClick={handleTPSubmit}>Submit</Button>
      </Controls>

      {result && (
        <Result success={result.success}>
          {result.message.split('\n').map((line, i) => (
            <div key={i}>{line}</div>
          ))}
        </Result>
      )}
    </Container>
  );
}

export default App;
